public static void extractAndStoreImageSrc(String html) {
    try {
        FileWriter writer = new FileWriter("image_src.txt");
        Pattern pattern = Pattern.compile("<imgsrc='([^']+)'");
        Matcher matcher = pattern.matcher(html);
        while (matcher.find()) {
            String src = matcher.group(1);
            writer.write(src + "\n");
        }
        writer.close();
        System.out.println("Image src attributes extracted and stored in image_src.txt");
    } catch (IOException e) {
        e.printStackTrace();
    }
}
